/* INDEX HOVER */

/* hover menu */
.overlay {
    position: fixed;
    top: 75px;
    width: 100%;
    left: 0;
    height: calc(100vh - 75px);
    background: rgba(0, 0, 0, 0.5);
    z-index: 2;
    opacity: 0;
    transition: opacity 0.2s ease-in;
    pointer-events: none;
}

.overlay .menu {
    position: absolute;
    right: 0;
    top: 0;
    width: 232px;
    height: 100%;
    max-height: calc(100vh - 75px);
    background: #FFFFFF;
    padding-left: 34px;
    padding-top: 37px;
    padding-right: 17px;
    margin-right: -232px;
    transition: right 0.2s ease-in;

}

.close {
    border: none;
    background: none;
    position: absolute;
    top: 16px;
    right: 16px;
    height: 12px;
    width: 12px;
}

.overlay.open {
    pointer-events: all;
    opacity: 1;
}


.overlay.open .menu {
    margin-right: 0;
}


/* menu */
.menu__header {
    font-weight: 700;
    font-size: 14px;
    line-height: 17px;
    color: #000000;
    margin-bottom: 24px;
}

.menu__list_main,
